inheritance.code:
class Animal {
public:
    void speak() {
        cout << 'Animal sound';
    }
};
class Dog : public Animal {
public:
    void speak() {
        cout << 'Woof!';
    }
};
int main() {
    Dog d;
    d.speak();
    return 0;
}

polymorphism.code:
class Shape {
public:
    virtual void draw() {
        cout << 'Drawing a shape';
    }
};
class Circle : public Shape {
public:
    void draw() {
        cout << 'Drawing a circle';
    }
};
int main() {
    Shape* s = new Circle();
    s->draw();
    delete s;
    return 0;
}

