If_Else_Statements.Code:
age = 18
if age >= 18:
    print('You are an adult.')
else:
    print('You are a minor.')

Nested_Loops.Code:
for i in range(3):
    for j in range(2):
        print(f'({i},{j})')

Classes_and_Objects.Code:
class Person:
    def __init__(self, name, age):
        self.name = name
        self.age = age
    def greet(self):
        return f'Hi, I\'m {self.name}.'
p = Person('Alice', 25)
print(p.greet())

Inheritance.Code:
class Animal:
    def __init__(self, name):
        self.name = name
    def speak(self):
        return 'Animal sounds'

class Dog(Animal):
    def speak(self):
        return 'Woof!'

dog = Dog('Rex')
print(dog.speak())

Try_Except_Block.Code:
try:
    result = 10 / 0
except ZeroDivisionError:
    print('Cannot divide by zero!')

Else_and_Finally.Code:
try:
    x = 5 / 0
except ZeroDivisionError:
    print('Error!')
else:
    print('No errors')
finally:
    print('Execution complete')

Decorators.Code:
def decorator(func):
    def wrapper():
        print('Before function call')
        func()
        print('After function call')
    return wrapper

@decorator
def say_hello():
    print('Hello!')
say_hello()
